package books.java_main_service.controller;import books.java_main_service.model.Deal;import books.java_main_service.model.TotalDeals;import books.java_main_service.service.DealService;import io.swagger.v3.oas.annotations.Parameter;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.*;import java.util.List;@RestController@RequestMapping("/api/admin/deals")public class AdminDealController {    private final DealService dealService;    @Autowired    public AdminDealController(DealService dealService) {        this.dealService = dealService;    }    @GetMapping("/statistics")    public ResponseEntity<List<TotalDeals>> getAllTotalDeals(            @Parameter(hidden = true) @RequestHeader("Authorization") String accessToken) {        return ResponseEntity.ok(dealService.getAllTotalDeals(accessToken));    }    @GetMapping("/user/{userId}")    public ResponseEntity<List<Deal>> getUserDeals(            @Parameter(hidden = true) @RequestHeader("Authorization") String accessToken,            @PathVariable Long userId) {        return ResponseEntity.ok(dealService.getAllDeals(accessToken, userId));    }}