package books.java_main_service.model;import jakarta.persistence.*;import jakarta.validation.constraints.*;import lombok.AllArgsConstructor;import lombok.Getter;import lombok.NoArgsConstructor;import lombok.Setter;import org.hibernate.annotations.OnDelete;import org.hibernate.annotations.OnDeleteAction;@Entity@Getter@Setter@NoArgsConstructor@AllArgsConstructor@Table(name = "book_items")public class BookItem {    public enum BookStatus {        AVAILABLE,        CHECKED_OUT,        RESERVED    }    public enum BookCondition {        FACTORY_NEW,        MINIMAL_WEAR,        FIELD_TESTED,        WELL_WORN,        BATTLE_SCARRED    }    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    private Long id;    @ManyToOne    @JoinColumn(name = "book_id", nullable = false)    @OnDelete(action = OnDeleteAction.CASCADE)    private Book book;    @Column(name = "owner_id", nullable = false)    @Positive(message = "ID владельца должен быть положительным числом")    private Long ownerId;    @Enumerated(EnumType.STRING)    @Column(nullable = false)    @NotNull(message = "Состояние книги не может быть null")    private BookCondition condition = BookCondition.MINIMAL_WEAR;    @Column(name = "available_for_deal", nullable = false)    @NotNull(message = "Доступность для обмена должна быть указана")    private Boolean availableForDeal;    @Column(name = "location", nullable = false, length = 100)    @NotBlank(message = "Местоположение не может быть пустым")    @Size(min = 2, max = 100, message = "Местоположение должно содержать от 2 до 100 символов")    private String location;    @Column(name = "description", nullable = false, length = 500)    @Size(max = 500, message = "Описание не должно превышать 500 символов")    private String description;    @Enumerated(EnumType.STRING)    @Column(nullable = false)    @NotNull(message = "Статус книги не может быть null")    private BookStatus status = BookStatus.AVAILABLE;}