package books.java_main_service.model;import com.fasterxml.jackson.annotation.JsonIgnore;import jakarta.persistence.*;import jakarta.validation.constraints.NotBlank;import jakarta.validation.constraints.Pattern;import jakarta.validation.constraints.Size;import lombok.AllArgsConstructor;import lombok.Getter;import lombok.NoArgsConstructor;import lombok.Setter;import java.util.HashSet;import java.util.Set;@Entity@Getter@Setter@NoArgsConstructor@AllArgsConstructor@Table(name = "genres")public class Genre {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    private Long id;    @Column(nullable = false, unique = true, length = 50)    @NotBlank(message = "Название жанра не может быть пустым")    @Size(min = 2, max = 50, message = "Название жанра должно содержать от 2 до 50 символов")    private String name;    @Column(nullable = false)    private boolean isMain = false;    @ManyToMany(mappedBy = "genres")    @JsonIgnore    private Set<Book> books = new HashSet<>();}