package books.java_main_service.model;import com.fasterxml.jackson.annotation.JsonBackReference;import jakarta.persistence.*;import jakarta.validation.constraints.*;import lombok.*;import org.hibernate.annotations.CreationTimestamp;import java.time.LocalDateTime;@Entity@Getter@Setter@NoArgsConstructor@AllArgsConstructor@Table(name = "reviews")public class Review {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    private Long id;    @Column(name = "user_id", nullable = false)    @Positive(message = "ID пользователя должен быть положительным числом")    private Long userId;    @ManyToOne    @JoinColumn(name = "book_id", nullable = false)    @JsonBackReference    private Book book;    @Column(nullable = false)    @NotNull(message = "Оценка не может быть пустой")    @Min(value = 1, message = "Минимальная оценка - 1")    @Max(value = 5, message = "Максимальная оценка - 5")    private Integer rating;    @Column(length = 1000)    @Size(max = 1000, message = "Отзыв не должен превышать 1000 символов")    private String description;    @CreationTimestamp    @Column(name = "created_at", nullable = false, updatable = false)    private LocalDateTime createdAt;}