package books.java_main_service.model;import jakarta.persistence.*;import lombok.Getter;import lombok.NoArgsConstructor;import lombok.Setter;@Entity@Getter@Setter@NoArgsConstructor@Table(name = "total_deals")public class TotalDeals {    @Id    @Column(nullable = false)    private Long userId;    @Column(nullable = false)    private Integer totalDeals = 0;    @Column(nullable = false)    private Integer successfulDeals = 0;    public TotalDeals(Long userId) {        this.userId = userId;        this.totalDeals = 0;        this.successfulDeals = 0;    }}