package books.java_main_service.model;import jakarta.validation.constraints.NotBlank;import jakarta.validation.constraints.Size;import lombok.AllArgsConstructor;import lombok.Getter;import lombok.NoArgsConstructor;import lombok.Setter;@Getter@Setter@AllArgsConstructor@NoArgsConstructorpublic class UserLoginDTO {    @NotBlank(message = "Login is required")    @Size(max = 25, message = "Login must not exceed 25 characters")    private String login;    @NotBlank(message = "Password is required")    @Size(max = 64, message = "Password must not exceed 64 characters")    private String password;}