package books.java_main_service.model;import jakarta.validation.constraints.Email;import jakarta.validation.constraints.NotBlank;import jakarta.validation.constraints.Pattern;import jakarta.validation.constraints.Size;import lombok.AllArgsConstructor;import lombok.Getter;import lombok.NoArgsConstructor;import lombok.Setter;@Getter@Setter@AllArgsConstructor@NoArgsConstructorpublic class UserProfileDTO {    private Long id;    @NotBlank(message = "Login is required")    @Size(max = 25, message = "Login must not exceed 25 characters")    private String login;    private String role;    @Email(message = "Mail cannot be invalid")    @Size(max = 255, message = "Mail must not exceed 255 characters")    private String mail;    @Pattern(            regexp = "^\\+?[0-9]{10,20}$",            message = "Phone number must contain up to 20 digits and may start with a '+'")    @Size(max = 20, message = "Phone number must not exceed 20 characters")    private String phone;}