package books.java_main_service.model;import jakarta.validation.constraints.NotBlank;import jakarta.validation.constraints.Size;import lombok.AllArgsConstructor;import lombok.Getter;import lombok.NoArgsConstructor;import lombok.Setter;@Getter@Setter@AllArgsConstructor@NoArgsConstructorpublic class UserUpdatePasswordDTO {    @NotBlank(message = "oldPassword is required")    @Size(max = 64, message = "oldPassword must not exceed 64 characters")    private String oldPassword;    @NotBlank(message = "NewPassword is required")    @Size(max = 64, message = "NewPassword must not exceed 64 characters")    private String newPassword;}