package books.java_main_service.repository;import books.java_main_service.model.BookItem;import org.springframework.data.jpa.repository.JpaRepository;import org.springframework.data.jpa.repository.Query;import org.springframework.data.repository.query.Param;import org.springframework.stereotype.Repository;import java.util.List;import java.util.Optional;@Repositorypublic interface BookItemRepository extends JpaRepository<BookItem, Long> {    List<BookItem> findByBookId(Long bookId);    Optional<BookItem> findByIdAndBookId(Long id, Long bookId);    @Query(            value =                    """    SELECT bi.* FROM book_items bi    WHERE bi.book_id = :bookId    AND (        (:location IS NULL AND :condition IS NULL) OR        (:location IS NOT NULL AND :condition IS NULL AND LOWER(bi.location) LIKE LOWER(CONCAT('%', :location, '%'))) OR        (:location IS NULL AND :condition IS NOT NULL AND bi.condition = :condition) OR        (:location IS NOT NULL AND :condition IS NOT NULL AND            LOWER(bi.location) LIKE LOWER(CONCAT('%', :location, '%')) AND            bi.condition = :condition)    )    """,            nativeQuery = true)    List<BookItem> searchByBookIdLocationAndCondition(            @Param("bookId") Long bookId,            @Param("location") String location,            @Param("condition") String condition);    boolean existsByBookIdAndOwnerId(Long bookId, Long ownerId);}