package books.java_main_service.repository;import books.java_main_service.model.Deal;import org.springframework.data.jpa.repository.JpaRepository;import org.springframework.data.jpa.repository.Query;import org.springframework.stereotype.Repository;import java.util.List;@Repositorypublic interface DealRepository extends JpaRepository<Deal, Long> {    List<Deal> findByInitiatorIdOrReceiverId(Long initiatorId, Long receiverId);    @Query("""    SELECT CASE WHEN COUNT(d) > 0 THEN true ELSE false END    FROM Deal d    WHERE (d.initiatorItem.id = :initiatorItemId OR d.receiverItem.id = :receiverItemId)      AND d.status IN :statuses""")    boolean existsActiveDeal(            Long initiatorItemId, Long receiverItemId, List<Deal.DealStatus> statuses);}