package books.java_main_service.repository;import books.java_main_service.model.Genre;import org.springframework.data.jpa.repository.JpaRepository;import org.springframework.stereotype.Repository;import java.util.List;@Repositorypublic interface GenreRepository extends JpaRepository<Genre, Long> {    List<Genre> findByIsMainTrue();    List<Genre> findByNameContainingIgnoreCase(String query);}